    name: "Test instance",
    plan: "penguin-1",
    region: "amazon-web-services::us-west-1",
    rmqVersion: "3.11.5",
    tags: ["aws"],
});
const _this = new cloudamqp.IntegrationAwsEventbridge("this", {
    instanceId: instance.id,
    vhost: instance.vhost,
    queue: "<QUEUE-NAME>",
    awsAccountId: "<AWS-ACCOUNT-ID>",
    awsRegion: "us-west-1",
    withHeaders: true,
});
